INSERT INTO `wp_eqv5w1_posts` VALUES(594, 2, '2009-11-22 15:32:05', '0000-00-00 00:00:00', '<p style="text-align:center;"><em>This is a new beginning\nA fresh start\nA new spoken shalom\nOver this weary, frail\nHumanity that I call a\nTemporary home.\n\nMy soul resembles water.\nNestled between the large\nCrevices of land to land;\nLand to land.\nIts meshing connection with\nThe Sky\nIs my connection with\nMy God\nYou cannot see the beginning or\nThe end.</em>\n<p style="text-align:center;">Â </p>\n\n<em>Â </em>\n\n<a href="http://www.flickr.com/photos/elle_effect/2646904396/" title="rest awhile by elle_effect, on Flickr"><img src="http://farm4.static.flickr.com/3291/2646904396_5b556bc894.jpg" width="500" height="332" alt="rest awhile" /></a>\n\n<a href="http://www.flickr.com/photos/elle_effect/2642944509/" title="You do not have a soul. You are a soul. You have a body. by elle_effect, on Flickr"><img src="http://farm4.static.flickr.com/3255/2642944509_034a44dfc4.jpg" width="500" height="332" alt="You do not have a soul. You are a soul. You have a body." /></a>\n\n<a href="http://www.flickr.com/photos/elle_effect/2642944969/" title="Ayla by elle_effect, on Flickr"><img src="http://farm4.static.flickr.com/3128/2642944969_7bf27b9fde.jpg" width="500" height="332" alt="Ayla" /></a>\n\n<a href="http://www.flickr.com/photos/elle_effect/2627254914/" title="sunrise; 5AM by elle_effect, on Flickr"><img src="http://farm4.static.flickr.com/3074/2627254914_1e67cdaf23.jpg" width="500" height="332" alt="sunrise; 5AM" /></a>', '', '', 'draft', 'open', 'open', '', '', '', '', '2009-11-22 15:32:05', '0000-00-00 00:00:00', '', 0, 'http://laurensusanna.wordpress.com/?p=594', 0, 'post', '', 0);
