INSERT INTO `wp_eqv5w1_posts` VALUES(915, 2, '2009-02-12 04:43:55', '2009-02-12 04:43:55', '<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3272901829_c80e9ffa9a.jpg" alt="" />\nHannah Schroeder, San Jose, CA: Tracking of Andrew''s record\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3272901545_fd7e30cbb6.jpg" alt="" />\nNacho, West Virginia: Winter\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3272901079_0870b25085.jpg" alt="" />\nJustin Yates, Felton, CA: September\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273721198_3b89997566.jpg" alt="" />\nDaniel Dixon (or Huckleberry Finn), Mountains of West Virginia: Spring\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273720856_8868450cd5.jpg" alt="" />\nJessica Grady, Wilson, NC: Spring\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273721108_6600997753.jpg" alt="" />\nJustin Yates, Felton, CA: September\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273720216_734ee55a3f.jpg" alt="" />\nAndrew and Brittany, Wilmington, NC: Spring\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273720470_86438ae8d2.jpg" alt="" />\nDaniel Dixon, Felton, CA: Tracking of Andrew''s Record\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3273721594_eecfb73402.jpg" alt="" />\nToo Cute Not to Post: Andrew &amp; Mom, Mountains of West Virginia: August\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://i281.photobucket.com/albums/kk213/elle_effect/3272904355_edee55a61b.jpg" alt="" />\n<a href="http://www.neighbourgoodsmarket.co.za/aboutcontact/"> Old Biscuit Mill: Neighbourgoods</a>, Cape Town, South Africa: November', 'Unseen Photos of 2008 - Pt 1', '', 'publish', 'open', 'open', '', 'unseen-photos-of-2008-pt-1', '', '', '2009-02-12 04:43:55', '2009-02-12 04:43:55', '', 0, 'http://laurensusanna.wordpress.com/?p=915', 0, 'post', '', 5);
INSERT INTO `wp_eqv5w1_posts` VALUES(919, 2, '2009-02-25 04:08:11', '2009-02-25 04:08:11', '<p style="text-align:center;"><strong>Rough mixing for Andrew''s album in Berkeley and San Francisco, CA.</strong>\n(much more on this later!)\n\n<p style="text-align:center;"><img class="aligncenter" src="http://farm4.static.flickr.com/3626/3308420458_6dab8730b6.jpg" alt="" />\n<img src="http://farm4.static.flickr.com/3529/3308425354_45360ed5ea.jpg" alt="" />\n<p style="text-align:center;"><img class="aligncenter" src="http://farm4.static.flickr.com/3404/3308439422_fb1ae269fd.jpg" alt="" /></p>\n<p style="text-align:center;"><img class="aligncenter" src="http://farm4.static.flickr.com/3340/3278150024_c682c53179.jpg" alt="" /></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://farm4.static.flickr.com/3376/3307608545_6c2bae1541.jpg" alt="" />\nTired faces at airports. Midnight with a 3+ hr drive home.\n<p style="text-align:center;"></p>\n<p style="text-align:center;"><img style="text-align:center;" src="&lt;/p&gt; &lt;p style=" alt="" /><strong>I was a single, working parent nannying two of the most lovely, creative girls, Allison and Sarah.</strong></p>\n\n<p style="text-align:center;"><img class="aligncenter" src="http://farm4.static.flickr.com/3420/3308390714_c7a6750335.jpg" alt="" />\n<img src="http://farm4.static.flickr.com/3350/3307559741_6c1f55f9ab.jpg" alt="" />\n<p style="text-align:center;"><img src="http://farm4.static.flickr.com/3557/3307559837_fa7733b012.jpg" alt="" /></p>\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><strong>I''m searching for a studio apartment.</strong> Some luck... just some.\n<strong>\nI said goodbye to my dear friend, John, for 8 months while he''s in Australia and traveling the world.</strong>\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><strong>I finished reading</strong> <em>The Art of the Commonplace</em> (Wendell Berry), <strong>finally.</strong>\nIt''s not the type of book to skim or read straight through.\n<img src="http://farm4.static.flickr.com/3539/3308264402_4b34d63593.jpg" alt="" />\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;">I''m in transition, I''m acclimating, I''m simplifying, I''m discouraged and encouraged all the same, I''m discontent, I''m searching for the next step(s), I''m seeking continually and I''m in Love.\n\nSorry for the lack of updates, I''ll do better.', 'The Past 2 Weeks:', '', 'publish', 'open', 'open', '', 'the-past-2-weeks', '', '', '2009-02-25 04:08:11', '2009-02-25 04:08:11', '', 0, 'http://laurensusanna.wordpress.com/?p=919', 0, 'post', '', 3);
