INSERT INTO `wp_eqv5w1_posts` VALUES(1118, 2, '2009-10-03 14:16:49', '2009-10-03 14:16:49', '<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><strong>ATT: Charleston, WV!\n</strong><strong></strong></p>\n<p style="text-align:center;"><img class="aligncenter" src="http://farm3.static.flickr.com/2429/3977181384_ba3dae95bf_o.jpg" alt="" width="568" height="758" /></p>\n<p style="text-align:center;"></p>\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;">Outdoor, on-location sessions.\nYou can add a pose for $10.\n<em>Email me with serious inquiries</em>:\n<strong>Sessions are filling quickly!</strong></p>\n\n<p style="text-align:center;">\nFriday<strong> +</strong> Saturday <strong>+</strong> Sunday</p>\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;"><em>Senior Portrait Sessions are also available to be scheduled.\nIf sessions fill, the next scheduling time will be mid-Nov! </em></p>\n\n<p style="text-align:center;">Photos will be ready for you in 2-3 weeks after the session!\nPerfect time for a Christmas Card family portrait : )</p>\n<p style="text-align:center;"></p>\n\n<p style="text-align:center;">See you soon!\nLauren</p>', 'Fall Family Portraits!', '', 'publish', 'open', 'open', '', 'fall-family-portraits', '', '', '2009-10-03 14:16:49', '2009-10-03 14:16:49', '', 0, 'http://elleeffect.com/?p=1118', 0, 'post', '', 0);
